#include <iostream>
#include <unordered_map>
#include <queue>
#include <string>
#include <vector>

using namespace std;

class Solution {
public:
    bool findWord(string s, vector<string>& wordDict) {
        for (auto wit = wordDict.begin(); wit != wordDict.end();) {
			string w = *wit;
            bool rf = true;
            bool lf = true;
            size_t pos = s.find(w);
            if (pos != string::npos) {
                size_t lpos = s.length();
                size_t rpos = 0;
                if (pos + w.length() != s.length()) {
                    rpos = pos + w.length();
                    rf = findWord(s.substr(rpos), wordDict);
                }
                if (pos != 0) {
                    lpos = 0;
                    lf = findWord(s.substr(0, pos), wordDict);
                }
            } else {
				wit++;
                continue;
            }
            if (lf && rf) {
                return true;
            }
        }
        return false;
    }
    bool wordBreak(string s, vector<string>& wordDict) {
		// prune
        for (auto wit = wordDict.begin(); wit != wordDict.end();) {
			string w = *wit;
            size_t pos = s.find(w);
            if (pos == string::npos) {
				wit = wordDict.erase(wit);
			} else {
				wit++;
			}
		}
        return findWord(s, wordDict);
    }


    bool wordBreak2(string s, vector<string>& wordDict) {
		queue<int> q;
		vector<int> visited(s.size());
		for (auto v: visited) {
			cout << v << endl;
		}
		unordered_map<string, string> m;

		for (auto w: wordDict) {
			m[w] = w;
		}
		q.push(0);
		while (!q.empty()) {
			int start = q.front();
			q.pop();
			if (visited[start] == 0) {
				for (int end = start + 1; end <= s.length(); end++) {
					cout << "start=" << start << " end=" << end << " " << s.substr(start, end - start);
					if (m.find(s.substr(start, end - start)) != m.end()) {
						cout << " FOUND";
						q.push(end);
						if (end == s.length()) {
							cout << endl;
							return true;
						}
					}
					cout << endl;
				}
				visited[start] = 1;
			}
		}
		return false;
	}
};


